import React, { useState } from "react"
import styled from 'styled-components'
import {PostData} from '../services/PostData'
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { Navigate } from 'react-router-dom';

const LoginBox = () => {
  const [ErrorLogin, setErrorLogin] = useState('');
  const [SuccessLogin, setSuccessLogin] = useState('');

  // Class with functionality to send/submit a form
  const formik = useFormik({
     initialValues: {
       username: '',
       password:'',

     },
     validationSchema: Yup.object({
      username: Yup.string().label('Email').email().required(),
      password: Yup.string()
        .label('Password')
        .required('Required!')
        .min(7, 'Seems a bit short...'),
    }),
     onSubmit: (values) => {
      if (values.username && values.password) {
        PostData(values).then((result) => {
          let responseJSON:any= result;
          console.log(responseJSON);

          if (responseJSON.status===200) {
            localStorage.setItem('token', responseJSON.data.token);
            setSuccessLogin('True');
          } else {
            alert('Login Error')
            console.log(PostData.Data)
            setErrorLogin(responseJSON.data.non_field_errors[0]);

            setTimeout(() => {
              setErrorLogin("")
            }, 10000); 
          }
        });
      }
    },
  });

  //  if (localStorage.getItem('token')) {
  //   return <Navigate to={'/'} />;
  // }
  if (SuccessLogin === 'True') {
     alert('Logged IN')

     return < Navigate to={'/'} /> 
  } 

  return (

        <Wrapper>
        <div className="jumbotron" style={{background:'none'}}>
          <div className="container text-center text-lg-left">
          <div className="row d-flex justify-content-center">
          <div className="col-md-5">
            <Box>
            <form id="loginform" onSubmit={loginSubmit}>
            <div className="text-center">
          <img src="Assets/Images/user.png" className="img-fluid profile-image-pic img-thumbnail rounded-circle my-3"
            width="160px" alt="profile"/>
        </div>
              <div className="form-group">
                <label>Email address</label>
                <input
                  type="email"
                  className="form-control"
                  id="EmailInput"
                  name="EmailInput"
                  aria-describedby="emailHelp"
                  placeholder="Enter email"
                  onChange={(event) => setEmail(event.target.value)}
                />
                <small id="emailHelp" className="text-danger form-text">
                  {emailError}
                </small>
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="exampleInputPassword1"
                  placeholder="Password"
                  onChange={(event) => setPassword(event.target.value)}
                />
                <small id="passworderror" className="text-danger form-text">
                  {passwordError}
                </small>
              </div>
              <div className="form-group form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id="exampleCheck1"
                />
                <label className="form-check-label">Check me out</label>
              </div>
              <button type="submit" className="btn btn-primary" style={{background:'#40798C'}}>
                Submit
              </button>
              <div id="emailHelp" className="form-text text-center mb-3 text-dark" style={{margin:'10px'}}> 
          Not Registered? 
          <a href="/signup" class="text-dark fw-bold"> 
          {" "}
          Create an Account</a>
        </div>
            </form>
            </Box>
          </div>

        </div>
          </div>
        </div>
      </Wrapper>);

  };

  export default LoginBox;

  const Wrapper = styled.section`
  margin-top:-25px;
padding: 4em;
min-height:700px;
background-image: url(/assets/Images/background.jpg);
background-size:cover;
background-blend-mode: multiply;
box-shadow: inset 0 0 0 2000px rgb(0 0 0 / 50%);
color:#fff;
`;
const Box = styled.section`
border-radius: 25px;
  padding: 4em;
  color:#000;
  background:#f0f0f0;
`;
